#include <iostream>
using namespace std;

double calculateBankFee(int count, double fee) {
    return count * fee;
}

int main() {
    cout << calculateBankFee(5, 2.0) << endl;
    cout << calculateBankFee(0, 3.0) << endl;
    cout << calculateBankFee(10, 1.5) << endl;
    return 0;
}

#include <iostream>
using namespace std;

bool isFever(float temp) {
    return temp > 37.5;
}

int main() {
    cout << isFever(38.0) << endl;
    cout << isFever(36.8) << endl;
    cout << isFever(37.6) << endl;
    return 0;
}

#include <iostream>
using namespace std;

double calculateCartTotal(double prices[], int n) {
    double sum = 0;
    for(int i = 0; i < n; i++) {
        sum += prices[i];
    }
    return sum;
}

int main() {
    double prices1[] = {10, 20, 30};
    double prices2[] = {};
    double prices3[] = {5.5, 4.5};
    cout << calculateCartTotal(prices1, 3) << endl;
    cout << calculateCartTotal(prices2, 0) << endl;
    cout << calculateCartTotal(prices3, 2) << endl;
    return 0;
}

#include <iostream>
using namespace std;

int updateScore(int current, int points) {
    return current + points;
}

int main() {
    cout << updateScore(100, 20) << endl;
    cout << updateScore(50, -10) << endl;
    cout << updateScore(0, 5) << endl;
    return 0;
}

