class Solution:
    def twoSum(self, nums, target):
        seen = {}
        for i, num in enumerate(nums):
            complement = target - num
            if complement in seen:
                return [seen[complement], i]
            seen[num] = i
        return []

class Solution {
    mostFrequentTag(tags) {
        const freq = {};
        let maxTag = tags[0];
        let maxCount = 0;

        for (const tag of tags) {
            freq[tag] = (freq[tag] || 0) + 1;
            if (freq[tag] > maxCount) {
                maxCount = freq[tag];
                maxTag = tag;
            }
        }
        return maxTag;
    }
}

